"use strict";(self.webpackChunkjohnycho_dev=self.webpackChunkjohnycho_dev||[]).push([[7508],{9679:i=>{i.exports=JSON.parse('{"tag":{"label":"Database","permalink":"/docs/tags/db","description":"Database tag description","allTagsPath":"/docs/tags","count":18,"items":[{"id":"db/db-acid","title":"ACID \uc18d\uc131","description":"ACID\ub294 \uc6d0\uc790\uc131(Atomicity), \uc77c\uad00\uc131(Consistency), \uaca9\ub9ac\uc131(Isolation), \uc9c0\uc18d\uc131(Durability)\uc758 \uc57d\uc790\uc774\uba70, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud2b8\ub79c\uc7ad\uc158\uc774 \uc548\uc804\ud558\uac8c \uc218\ud589\ub41c\ub2e4\ub294 \uac83\uc744 \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \uc131\uc9c8\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4.","permalink":"/docs/db/db-acid"},{"id":"db/db-cap-theorem","title":"CAP \uc815\ub9ac (= \ube0c\ub8e8\uc5b4(Brewer)\uc758 \uc815\ub9ac)","description":"CAP \uc815\ub9ac (= \ube0c\ub8e8\uc5b4(Brewer)\uc758 \uc815\ub9ac)\ub294 \ubd84\uc0b0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc774 CAP \uc911 2\uac1c\uc758 \uc18d\uc131\ub9cc\uc744 \uc81c\uacf5\ud560 \uc218 \uc788\ub2e4\ub294 \uc774\ub860\uc785\ub2c8\ub2e4. CAP \uc815\ub9ac\uc5d0 \ub530\ub974\uc790\uba74, \uc77c\uad00\uc131(Consistency), \uac00\uc6a9\uc131(Availability), \ubd84\ud560 \ub0b4\uc131(Partition Tolerance) \ub4f1 3\uac00\uc9c0 \uc18d\uc131\uc744 \ubaa8\ub450 \ub9cc\uc871\ud558\ub294 \ubd84\uc0b0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc740 \uc874\uc7ac\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-cap-theorem"},{"id":"db/db-replication","title":"DB Replication","description":"DB Replication\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uace0\uac00\uc6a9\uc131\uacfc \ub370\uc774\ud130 \uc548\uc815\uc131\uc744 \ubcf4\uc7a5\ud558\uae30 \uc704\ud574 \ub110\ub9ac \ud65c\uc6a9\ub418\ub294 \ud575\uc2ec \uae30\uc220\uc785\ub2c8\ub2e4. \ud2b9\ud788, \ub300\uaddc\ubaa8 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \ud658\uacbd\uc5d0\uc11c\ub294 \ub370\uc774\ud130\uc758 \uc9c0\uc18d\uc801\uc778 \uac00\uc6a9\uc131\uacfc \uc2e0\ub8b0\uc131\uc774 \ub9e4\uc6b0 \uc911\uc694\ud558\uae30 \ub54c\ubb38\uc5d0, \uc6d0\ubcf8(Source) \uc11c\ubc84\uc640 \ubcf5\uc81c(Replica) \uc11c\ubc84 \uac04\uc758 \ub370\uc774\ud130 \ub3d9\uae30\ud654\ub294 \ud544\uc218\uc785\ub2c8\ub2e4. MySQL \uae30\uc900\uc73c\ub85c \uc124\uba85\ud558\uaca0\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-replication"},{"id":"db/db-mysql-innodb-lock","title":"MySQL InnoDB\uc758 \uac2d \ub77d\uacfc \ub125\uc2a4\ud2b8\ud0a4 \ub77d","description":"\u2714\ufe0f Phantom Read\ub780?","permalink":"/docs/db/db-mysql-innodb-lock"},{"id":"db/db-rdb-paging","title":"RDB \ud398\uc774\uc9d5 \ucffc\ub9ac(Paging Query)","description":"\ud398\uc774\uc9d5 \ucffc\ub9ac(Paging Query) \ub294 \uc804\uccb4 \ub370\uc774\ud130\ub97c \ubd80\ubd84\uc801\uc73c\ub85c \ub098\ub204\uc5b4 \ub370\uc774\ud130\ub97c \uc870\ud68c\ud558\uac70\ub098 \ucc98\ub9ac\ud560 \ub54c \uc0ac\uc6a9\ub429\ub2c8\ub2e4.","permalink":"/docs/db/db-rdb-paging"},{"id":"db/db-redis-single-thread","title":"Redis\uac00 \uc2f1\uae00 \uc2a4\ub808\ub4dc\ub85c \ub9cc\ub4e4\uc5b4\uc9c4 \uc774\uc720","description":"Redis\uac00 \ub2e8\uc77c \uc2a4\ub808\ub4dc(single-threaded)\ub85c \uc124\uacc4\ub41c \uc774\uc720\ub294 \uc8fc\ub85c \uc131\ub2a5 \ucd5c\uc801\ud654, \ubcf5\uc7a1\uc131 \uac10\uc18c, \uadf8\ub9ac\uace0 \ub370\uc774\ud130 \uc77c\uad00\uc131\uc744 \uc720\uc9c0\uc5d0 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-redis-single-thread"},{"id":"db/db-redis-distributed-lock","title":"Redis\ub97c \ud65c\uc6a9\ud55c \ubd84\uc0b0\ub77d \uad6c\ud604","description":"Redis\uc758 SET \uba85\ub839\uc5b4\ub97c \uc0ac\uc6a9\ud574\uc11c \ubd84\uc0b0 \uc7a0\uae08\uc744 \uad6c\ud604\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uac00\ub839, 1\ub300 \uc774\uc0c1\uc758 \uc11c\ubc84\uac00 \ud2b9\uc815 Key\uc5d0 \ub300\ud574\uc11c SET \uba85\ub839\uc5b4\uc5d0 NX \uc635\uc158(Not eXists)\uc744 \ucd94\uac00\ud558\uc5ec Redis\uc5d0 \uc804\ub2ec\ud558\uc5ec \uc7a0\uae08 \ud68d\ub4dd\uc744 \uc2dc\ub3c4\ud569\ub2c8\ub2e4. NX \uc635\uc158\uc744 \uc0ac\uc6a9\ud558\uba74 \ud2b9\uc815 Key\uc5d0 \ud574\ub2f9\ud558\ub294 \uac12\uc774 \uc874\uc7ac\ud558\uc9c0 \uc54a\ub294 \uacbd\uc6b0\uc5d0\ub9cc \uac12 \ucd94\uac00 \uc791\uc5c5\uc774 \uc131\uacf5\ud569\ub2c8\ub2e4. SET \uc791\uc5c5\uc744 \uc131\uacf5\uc801\uc73c\ub85c \uc218\ud589\ud55c \uc11c\ubc84\ub294 \uc7a0\uae08\uc744 \ud68d\ub4dd\ud569\ub2c8\ub2e4. \uc7a0\uae08\uc744 \ud68d\ub4dd\ud55c \uc11c\ubc84\ub294 \uc791\uc5c5\uc774 \ub05d\ub09c \uc774\ud6c4, Key\uc5d0 \ud574\ub2f9\ud558\ub294 \uac12\uc744 \uc81c\uac70\ud558\uc5ec \uc7a0\uae08\uc744 \ud574\uc81c\ud569\ub2c8\ub2e4.","permalink":"/docs/db/db-redis-distributed-lock"},{"id":"db/db-shared-exclusive-lock","title":"\uacf5\uc720 \ub77d(Shared Lock)\uacfc \ubc30\ud0c0 \ub77d(Exclusive Lock)","description":"DBMS\uc5d0\uc11c \ud2b8\ub79c\uc7ad\uc158\uc744 \ud2b9\ubcc4\ud55c \uc81c\uc5b4 \uc5c6\uc774 \ubcd1\ud589 \uc218\ud589\uc744 \ud5c8\uc6a9\ud55c\ub2e4\uba74 \ub370\uc774\ud130\uc758 \uc77c\uad00\uc131\uacfc \ubb34\uacb0\uc131\uc744 \ubcf4\uc7a5\ud558\uae30 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub54c, \ubcd1\ud589 \uc218\ud589\ub418\ub294 \ud2b8\ub79c\uc7ad\uc158\ub4e4\uc744 \uc81c\uc5b4\ud558\uae30 \uc704\ud574\uc11c \ub77d\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc73c\uba70 DBMS\uc5d0\uc11c \ub77d\uc740 \ud06c\uac8c \uacf5\uc720 \ub77d\uacfc \ubc30\ud0c0 \ub77d\uc73c\ub85c \ubd84\ub958\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-shared-exclusive-lock"},{"id":"db/db-rdb-and-nosql","title":"\uad00\uacc4\ud615 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc640 \ube44 \uad00\uacc4\ud615 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \ucc28\uc774\uc810","description":"\u2714\ufe0f \uad00\uacc4\ud615 \ub370\uc774\ud130\ubca0\uc774\uc2a4","permalink":"/docs/db/db-rdb-and-nosql"},{"id":"db/db-optimistic-pessimistic-lock","title":"\ub099\uad00\uc801 \ub77d\uacfc \ube44\uad00\uc801 \ub77d","description":"\ub099\uad00\uc801 \ub77d\uacfc \ube44\uad00\uc801 \ub77d\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud2b8\ub79c\uc7ad\uc158\uc5d0\uc11c \ub3d9\uc2dc\uc131 \uc81c\uc5b4\ub97c \uc704\ud55c \uc8fc\uc694 \uae30\ubc95\uc785\ub2c8\ub2e4.","permalink":"/docs/db/db-optimistic-pessimistic-lock"},{"id":"db/db-concurrency-control","title":"\ub370\uc774\ud130\ubca0\uc774\uc2a4 \ub3d9\uc2dc\uc131 \uc81c\uc5b4 \ubc29\ubc95","description":"\ub300\ud45c\uc801\uc778 \ub3d9\uc2dc\uc131 \uc81c\uc5b4 \ubc29\uc2dd\uc73c\ub85c MVCC(Multi-Version Concurrency Control) \uc640 Lock-Based Concurrency Control\uc774 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-concurrency-control"},{"id":"db/db-index","title":"\ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc778\ub371\uc2a4","description":"\ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud14c\uc774\ube14\uc758 \uac80\uc0c9 \uc18d\ub3c4\ub97c \ud5a5\uc0c1\uc2dc\ud0a4\uae30 \uc704\ud55c \uc790\ub8cc\uad6c\uc870\ub85c \ubc31\uacfc\uc0ac\uc804\uc758 \uc0c9\uc778\uacfc \uac19\uc2b5\ub2c8\ub2e4. \uc800\uc7a5\ub418\ub294 \uceec\ub7fc\uc758 \uac12\uc744 \uc0ac\uc6a9\ud558\uc5ec \ud56d\uc0c1 \uc815\ub82c\ub41c \uc0c1\ud0dc\ub97c \uc720\uc9c0\ud558\ub294 \uac83\uc774 \ud2b9\uc9d5\uc785\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ud2b9\uc9d5\uc73c\ub85c \uc778\ud574 \uc778\ub371\uc2a4\ub294 INSERT, UPDATE, DELETE\uc758 \uc131\ub2a5\uc774 \ud76c\uc0dd\ub41c\ub2e4\ub294 \uac83\uc774 \ub2e8\uc810\uc785\ub2c8\ub2e4.","permalink":"/docs/db/db-index"},{"id":"db/db-normalization","title":"\ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc815\uaddc\ud654","description":"\ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0\uc11c \uc815\uaddc\ud654(Normalization)\ub294 \ud14c\uc774\ube14\uc744 \uc815\ub9ac\ud558\uc5ec \uc911\ubcf5 \ub370\uc774\ud130\ub97c \ucd5c\uc18c\ud654\ud558\uace0, \ub370\uc774\ud130 \ubb34\uacb0\uc131\uc744 \ubcf4\uc7a5\ud558\ub294 \uacfc\uc815\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ub370\uc774\ud130 \uc800\uc7a5 \uc6a9\ub7c9\uc744 \uc904\uc774\uace0, \uc0bd\uc785\xb7\uac31\uc2e0\xb7\uc0ad\uc81c \uc774\uc0c1(Anomaly) \ud604\uc0c1\uc744 \ud574\uacb0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc815\uaddc\ud654\ub294 \uc5ec\ub7ec \ub2e8\uacc4\uac00 \uc874\uc7ac\ud558\uba70, \ub300\ud45c\uc801\uc73c\ub85c 1 \uc815\uaddc\ud654(1NF), 2 \uc815\uaddc\ud654(2NF), 3 \uc815\uaddc\ud654(3NF), BCNF\uac00 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-normalization"},{"id":"db/db-connection-pool","title":"\ub370\uc774\ud130\ubca0\uc774\uc2a4 \ucee4\ub125\uc158 \ud480","description":"\uc560\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \ub370\uc774\ud130\ubca0\uc774\uc2a4\uac00 \ud1b5\uc2e0\uc744 \ud558\uae30 \uc704\ud574\uc11c\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ucee4\ub125\uc158\uc774 \ud544\uc694\ud569\ub2c8\ub2e4.","permalink":"/docs/db/db-connection-pool"},{"id":"db/db-cache-stampede","title":"\uce90\uc2dc \uc2a4\ud0ec\ud53c\ub4dc \ud604\uc0c1","description":"\ub300\uaddc\ubaa8 \ud2b8\ub798\ud53d \ud658\uacbd\uc5d0\uc11c \uce90\uc2dc\ub97c \uc6b4\uc6a9\ud558\ub294\ub370, Cache Aside (\uce90\uc2dc \ubbf8\uc2a4 \ubc1c\uc0dd \uc2dc \uc801\uc7ac) \uc804\ub7b5\uc744 \uc0ac\uc6a9\ud55c\ub2e4\uace0 \uac00\uc815\ud558\uaca0\uc2b5\ub2c8\ub2e4. \uc774\ub54c, \uc218\ub9ce\uc740 \uc694\uccad\ub4e4\uc774 \ub3d9\uc2dc\uc5d0 \uce90\uc2dc \ubbf8\uc2a4\ub97c \ud655\uc778\ud558\uace0 \uc6d0\ubcf8 \uc800\uc7a5\uc18c\uc5d0\uc11c \ub370\uc774\ud130\ub97c \uac00\uc838\uc640 \uce90\uc2dc\uc5d0 \uc801\uc7ac\ud558\ub294 \uc0c1\ud669\uc774 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294\ub370\uc694. \uc774\ub97c \uce90\uc2dc \uc2a4\ud0ec\ud53c\ub4dc \ud604\uc0c1 \ud639\uc740 Thundering Herd \ubb38\uc81c\ub77c\uace0 \ud45c\ud604\ud569\ub2c8\ub2e4. \uce90\uc2dc \uc2a4\ud0ec\ud53c\ub4dc \ud604\uc0c1\uc740 \uc6d0\ubcf8 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc640 \uce90\uc2dc\uc758 \uc131\ub2a5\uc744 \uc800\ud558\ud560 \uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-cache-stampede"},{"id":"db/db-caching-strategy","title":"\uce90\uc2f1 \uc804\ub7b5","description":"\uce90\uc2dc\ub294 \uc131\ub2a5 \ud5a5\uc0c1\uacfc \ubd80\ud558 \uac10\uc18c\ub97c \ubaa9\ud45c\ub85c \ud569\ub2c8\ub2e4. \uc774\ub54c \uce90\uc2dc\ub97c \uc0ac\uc6a9\ud558\ub294 \uc591\uc0c1\uc774 \uc11c\ube44\uc2a4\uc5d0 \ud070 \uc601\ud5a5\uc744 \ub07c\uce58\uae30\ub3c4 \ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c, \uce90\uc2f1 \uc804\ub7b5\uc744 \uc774\ud574\ud558\ub294 \uac83\uc740 \uc911\uc694\ud569\ub2c8\ub2e4.","permalink":"/docs/db/db-caching-strategy"},{"id":"db/db-transactional-outbox-pattern","title":"\ud2b8\ub79c\uc7ad\uc154\ub110 \uc544\uc6c3\ubc15\uc2a4 \ud328\ud134 (Transactional Outbox Pattern)","description":"\ud2b8\ub79c\uc7ad\uc154\ub110 \uc544\uc6c3\ubc15\uc2a4 \ud328\ud134(Transactional Outbox Pattern) \uc740 \ubd84\uc0b0 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ub2e8\uc77c \uc791\uc5c5\uc5d0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc4f0\uae30 \uc791\uc5c5\uacfc \uba54\uc2dc\uc9c0 \ud639\uc740 \uc774\ubca4\ud2b8 \ubc1c\ud589\uc774 \ubaa8\ub450 \ud3ec\ud568\ub41c \uacbd\uc6b0 \ubc1c\uc0dd\ud558\ub294 \uc774\uc911 \uc4f0\uae30 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4, \ub2e4\uc74c\uacfc \uac19\uc740 \ucf54\ub4dc\uac00 \uc874\uc7ac\ud55c\ub2e4\uace0 \uac00\uc815\ud558\uaca0\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-transactional-outbox-pattern"},{"id":"db/db-transaction-isolation-level","title":"\ud2b8\ub79c\uc7ad\uc158 \uaca9\ub9ac\uc218\uc900","description":"\ub3d9\uc2dc\uc5d0 \uc5ec\ub7ec \ud2b8\ub79c\uc7ad\uc158\uc774 \uc2e4\ud589\ub420 \ub54c \ud55c \ud2b8\ub79c\uc7ad\uc158\uc774 \ub2e4\ub978 \ud2b8\ub79c\uc7ad\uc158\uc758 \uc5f0\uc0b0\uc5d0 \uc601\ud5a5\uc744 \ubc1b\uc9c0 \uc54a\ub3c4\ub85d \ud558\ub294 \uc815\ub3c4\ub97c \ub9d0\ud569\ub2c8\ub2e4. \ub0ae\uc740 \uaca9\ub9ac \uc218\uc900\uc740 \ub3d9\uc2dc \ucc98\ub9ac \ub2a5\ub825\uc744 \ub192\uc774\uc9c0\ub9cc, \ub370\uc774\ud130\uc758 \uc77c\uad00\uc131 \ubb38\uc81c\ub97c \ubc1c\uc0dd\uc2dc\ud0ac \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ubc18\uba74, \ub192\uc740 \uaca9\ub9ac \uc218\uc900\uc740 \ub370\uc774\ud130\uc758 \uc77c\uad00\uc131\uc744 \ubcf4\uc7a5\ud558\uc9c0\ub9cc, \ub3d9\uc2dc \ucc98\ub9ac \ub2a5\ub825\uc774 \ub5a8\uc5b4\uc9c8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc989, \ub370\uc774\ud130 \uc815\ud569\uc131\uacfc \uc131\ub2a5\uc740 \ubc18\ube44\ub840\ud569\ub2c8\ub2e4. \ud2b8\ub79c\uc7ad\uc158 \uaca9\ub9ac \uc218\uc900\uc740 \uac1c\ubc1c\uc790\uac00 \ud2b8\ub79c\uc7ad\uc158 \uaca9\ub9ac \uc218\uc900\uc744 \uc124\uc815\ud560 \uc218 \uc788\ub294 \uae30\ub2a5\uc744 \uc81c\uacf5\ud558\ub294 \uae30\ub2a5\uc785\ub2c8\ub2e4.","permalink":"/docs/db/db-transaction-isolation-level"}],"unlisted":false}}')}}]);