"use strict";(self.webpackChunkjohnycho_dev=self.webpackChunkjohnycho_dev||[]).push([[8040],{3651:n=>{n.exports=JSON.parse('{"tag":{"label":"Spring","permalink":"/docs/tags/spring","description":"Spring Framework tag description","allTagsPath":"/docs/tags","count":14,"items":[{"id":"spring-framework/spring-transactional-aop","title":"@Transactional \uc5b4\ub178\ud14c\uc774\uc158 AOP \ub3d9\uc791 \ud750\ub984","description":"@Transactional \uc5b4\ub178\ud14c\uc774\uc158\uc744 \uc0ac\uc6a9\ud55c \uc120\uc5b8\uc801 \ud2b8\ub79c\uc7ad\uc158 \uad00\ub9ac(Declarative Transaction Management)\uc758 \uc804\uccb4 \ud750\ub984\uc5d0\ub294 \ud06c\uac8c 3\uac00\uc9c0 \uc694\uc18c\uac00 \ub4f1\uc7a5\ud569\ub2c8\ub2e4. \ud2b8\ub79c\uc7ad\uc158 \ub9e4\ub2c8\uc800, \ud2b8\ub79c\uc7ad\uc158 AOP \ud504\ub85d\uc2dc, \ud2b8\ub79c\uc7ad\uc158 \ub3d9\uae30\ud654 \ub9e4\ub2c8\uc800\uac00 \uc774\uc5d0 \ud574\ub2f9\ub429\ub2c8\ub2e4.","permalink":"/docs/spring-framework/spring-transactional-aop"},{"id":"spring-data-jpa/jpa-ddl-auto","title":"ddl-auto \uc635\uc158","description":"ddl-auto \uc635\uc158\uc740 \uc2a4\ud504\ub9c1 \ubd80\ud2b8 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c Hibernate\uc640 \uac19\uc740 JPA \uad6c\ud604\uccb4\ub97c \uc0ac\uc6a9\ud560 \ub54c \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2a4\ud0a4\ub9c8 \uad00\ub9ac\ub97c \uc81c\uc5b4\ud558\ub294 \uc124\uc815\uc785\ub2c8\ub2e4.","permalink":"/docs/spring-data-jpa/jpa-ddl-auto"},{"id":"cs/cs-graceful-shutdown","title":"Graceful Shutdown (\uc6b0\uc544\ud55c \uc885\ub8cc)","description":"\u2714\ufe0f Graceful Shutdown (\uc6b0\uc544\ud55c \uc885\ub8cc)","permalink":"/docs/cs/cs-graceful-shutdown"},{"id":"spring-data-jpa/jpa-id-generation-strategy","title":"ID \uc0dd\uc131 \uc804\ub7b5","description":"JPA\uc5d0\uc11c ID\ub97c \uc0dd\uc131\ud558\uae30 \uc704\ud574\uc11c\ub294 \uc9c1\uc811 \ud560\ub2f9\uacfc \uc790\ub3d9 \ud560\ub2f9\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc9c1\uc811 \ud560\ub2f9\uc740 @Id\uc5b4\ub178\ud14c\uc774\uc158\ub9cc\uc744 \uc0ac\uc6a9\ud558\uc5ec Id\uac12\uc744 \uc9c1\uc811 \ud560\ub2f9\ud558\ub294 \ubc29\uc2dd\uc785\ub2c8\ub2e4. \ubc18\uba74, \uc790\ub3d9","permalink":"/docs/spring-data-jpa/jpa-id-generation-strategy"},{"id":"spring-data-jpa/jpa-n+1-problem","title":"N + 1 \ubb38\uc81c","description":"\uc5f0\uad00 \uad00\uacc4\uac00 \uc124\uc815\ub41c \uc5d4\ud2f0\ud2f0\ub97c \uc870\ud68c\ud560 \uacbd\uc6b0\uc5d0, \uc870\ud68c\ub41c \ub370\uc774\ud130 \uac1c\uc218(N)\ub9cc\ud07c \uc5f0\uad00\uad00\uacc4\uc758 \uc870\ud68c \ucffc\ub9ac\uac00 \ucd94\uac00\ub85c \ubc1c\uc0dd\ud558\ub294 \ud604\uc0c1\uc785\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4, \ube14\ub85c\uadf8 \uac8c\uc2dc\uae00\uacfc \ub313\uae00\uc774 \uc788\ub294 \uacbd\uc6b0,","permalink":"/docs/spring-data-jpa/jpa-n+1-problem"},{"id":"spring-data-jpa/jpa-one-to-one-lazy-loading","title":"OneToOne \uc5f0\uad00\uad00\uacc4 Lazy Loading \ubb38\uc81c","description":"\uc591\ubc29\ud5a5 @OneToOne\uc77c \ub54c \uc5f0\uad00\uad00\uacc4\uc758 \uc8fc\uc778\uc774 \uc544\ub2cc \uc5d4\ud2f0\ud2f0\ub97c \uc870\ud68c\ud560 \uacbd\uc6b0 Lazy Loading\uc774 \ub3d9\uc791\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.","permalink":"/docs/spring-data-jpa/jpa-one-to-one-lazy-loading"},{"id":"spring-framework/spring-private-transactional","title":"private \uba54\uc11c\ub4dc\uc5d0 @Transactional \uc120\uc5b8\ud558\uba74 \ud2b8\ub79c\uc7ad\uc158\uc774 \ub3d9\uc791\ud560\uae4c?","description":"\uae30\ubcf8\uc801\uc73c\ub85c @Transactional, @Cacheable, @Async \ub4f1\uc758 \uc560\ub108\ud14c\uc774\uc158\uc740 \ub7f0\ud0c0\uc784\uc5d0 \ub3d9\uc791\ud558\ub294 Spring AOP\ub97c \uae30\ubc18\uc73c\ub85c \ub3d9\uc791\ud569\ub2c8\ub2e4. Spring AOP\uac00 \uc81c\uacf5\ud558\ub294 JDK Dynamic Proxy, CGLIB \ubc29\uc2dd \ubaa8\ub450 \ud0c0\uae43\uc774 \uad6c\ud604\ud558\ub294 \uc778\ud130\ud398\uc774\uc2a4\ub098 \uad6c\uccb4 \ud074\ub798\uc2a4\ub97c \ub300\uc0c1\uc73c\ub85c \ud504\ub85d\uc2dc\ub97c \ub9cc\ub4e4\uc5b4\uc11c \ud0c0\uae43 \ud074\ub798\uc2a4\uc758 \uba54\uc11c\ub4dc \uc218\ud589 \uc804\ud6c4\uc5d0 \ud6a1\ub2e8 \uad00\uc2ec\uc0ac\uc5d0 \ub300\ud55c \ucc98\ub9ac\ub97c \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/spring-framework/spring-private-transactional"},{"id":"spring-framework/spring-bean-annotations","title":"Spring Bean \uc5b4\ub178\ud14c\uc774\uc158","description":"@Component, @Service, @Controller, @Repository\ub294 \uac01\uac01\uc758 \ud074\ub798\uc2a4\ub97c \ud2b9\uc815 \uc5ed\ud560\uc744 \uc218\ud589\ud558\ub294 Spring Bean\uc73c\ub85c \ub4f1\ub85d\ud560 \ub54c \uc0ac\uc6a9\ub429\ub2c8\ub2e4. \uac01 \uc560\ub108\ud14c\uc774\uc158\uc740 \ud074\ub798\uc2a4\uac00 \uc5b4\ub5a4 \uc5ed\ud560\uc744 \ud558\ub294\uc9c0\ub97c \uba85\uc2dc\uc801\uc73c\ub85c \ub098\ud0c0\ub0b4\uba70, Spring\uc758 @ComponentScan \uae30\ub2a5\uc744 \ud1b5\ud574 \uc790\ub3d9\uc73c\ub85c Bean\uc73c\ub85c \ub4f1\ub85d\ub429\ub2c8\ub2e4. @Service, @Controller, @Repository \uc5b4\ub178\ud14c\uc774\uc158\uc740 \ub0b4\ubd80\uc801\uc73c\ub85c @Component \uc5b4\ub178\ud14c\uc774\uc158\uc744 \uc0ac\uc6a9\ud558\uace0 \uc788\uc73c\uba70, \uac01 \ud2b9\uc9d5\uacfc \uc6a9\ub3c4\ub294 \uc544\ub798\uc640 \uac19\uc2b5\ub2c8\ub2e4.","permalink":"/docs/spring-framework/spring-bean-annotations"},{"id":"spring-framework/spring-actuator-micrometer","title":"Spring Boot Actuator\uc640 Micrometer","description":"\u2714\ufe0f Micrometer\ub780 \ubb34\uc5c7\uc774\uba70, \uc65c \uc0ac\uc6a9\ud560\uae4c?","permalink":"/docs/spring-framework/spring-actuator-micrometer"},{"id":"spring-framework/spring-transaction-propagation","title":"Spring \ud2b8\ub79c\uc7ad\uc158 \uc804\ud30c (Transaction Propagation)","description":"\uc2a4\ud504\ub9c1\uc5d0\uc11c \ud2b8\ub79c\uc7ad\uc158 \uc804\ud30c(Transaction Propagation) \ub294 \ud2b8\ub79c\uc7ad\uc158\uc758 \uacbd\uacc4\uc5d0\uc11c \uc774\ubbf8 \uc9c4\ud589 \uc911\uc778 \ud2b8\ub79c\uc7ad\uc158\uc774 \uc788\uc744 \ub54c \ub610\ub294 \uc5c6\uc744 \ub54c \uc5b4\ub5bb\uac8c \ub3d9\uc791\ud560 \uac83\uc778\uac00\ub97c \uacb0\uc815\ud558\ub294 \uae30\ub2a5\uc785\ub2c8\ub2e4. \uac00\ub839, @Transactional \uc5b4\ub178\ud14c\uc774\uc158\uc774 \uc874\uc7ac\ud558\ub294 \uba54\uc11c\ub4dc\ub97c \ud638\ucd9c\ud588\uc744 \ub54c, \uae30\uc874\uc5d0 \ud2b8\ub79c\uc7ad\uc158\uc774 \uc874\uc7ac\ud558\uba74 \uc7ac\uc0ac\uc6a9\ud560\uc9c0, \uc608\uc678\ub97c \ub358\uc9c8\uc9c0 \ub4f1 \ud589\ub3d9\uc744 \uacb0\uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/spring-framework/spring-transaction-propagation"},{"id":"cs/cs-unit-integration-test","title":"\ub2e8\uc704 \ud14c\uc2a4\ud2b8\uc640 \ud1b5\ud569 \ud14c\uc2a4\ud2b8","description":"\ub2e8\uc704 \ud14c\uc2a4\ud2b8","permalink":"/docs/cs/cs-unit-integration-test"},{"id":"spring-data-jpa/jpa-new-entity","title":"\uc0c8\ub85c\uc6b4 Entity \ud310\ub2e8 \uae30\uc900","description":"\u2714\ufe0f JPA\uc5d0\uc11c \uc0c8\ub85c\uc6b4 Entity\uc778\uc9c0 \ud310\ub2e8\ud558\ub294 \ubc29\ubc95\uc740 \ubb34\uc5c7\uc77c\uae4c?","permalink":"/docs/spring-data-jpa/jpa-new-entity"},{"id":"spring-data-jpa/jpa-entity-manager","title":"\uc5d4\ud2f0\ud2f0 \ub9e4\ub2c8\uc800(Entity Manager)","description":"\uc5d4\ud2f0\ud2f0 \ub9e4\ub2c8\uc800\uc5d0 \ub300\ud574 \uc54c\uae30 \uc704\ud574\uc120 \uc601\uc18d\uc131 \ucee8\ud14d\uc2a4\ud2b8\uc5d0 \ub300\ud574 \uc54c\uc544\uc57c \ud569\ub2c8\ub2e4.","permalink":"/docs/spring-data-jpa/jpa-entity-manager"},{"id":"spring-framework/spring-dependency-injection","title":"\uc758\uc874\uc131 \uc8fc\uc785 (Dependency Injection)","description":"A \uac1d\uccb4\uac00 \uc5b4\ub5a4 \uc791\uc5c5\uc744 \uc218\ud589\ud558\uae30 \uc704\ud574 B \uac1d\uccb4\ub97c \ud544\uc694\ub85c \ud558\ub294 \uacbd\uc6b0\uc5d0 \ub450 \uac1d\uccb4 \uc0ac\uc774\uc5d0 \uc758\uc874\uc131\uc774 \uc874\uc7ac\ud55c\ub2e4\uace0 \ud45c\ud604\ud569\ub2c8\ub2e4. \uc774\ub54c, A \uac1d\uccb4\uac00 \uc544\ub2cc \uc678\ubd80\uc758 C \uac1d\uccb4\uac00 B\ub97c \uc0dd\uc131\ud55c \ub4a4\uc5d0 \uc774\ub97c \uc804\ub2ec\ud574\uc11c \uc758\uc874\uc131\uc744 \ud574\uacb0\ud558\ub294 \ubc29\ubc95\uc744 \uc758\uc874\uc131 \uc8fc\uc785(Dependency Injection) \uc774\ub77c\uace0 \ud569\ub2c8\ub2e4.","permalink":"/docs/spring-framework/spring-dependency-injection"}],"unlisted":false}}')}}]);