"use strict";(self.webpackChunkjohnycho_dev=self.webpackChunkjohnycho_dev||[]).push([[9916],{4612:s=>{s.exports=JSON.parse('{"tag":{"label":"Computer Science","permalink":"/docs/tags/cs","description":"Computer Science tag description","allTagsPath":"/docs/tags","count":61,"items":[{"id":"cs/cs-call-by","title":"Call By Value\uc640 Call By Reference","description":"\ud2b9\uc815 \uba54\uc11c\ub4dc\ub97c \ud638\ucd9c\ud558\ub294 \uacbd\uc6b0 \uc778\uc790\ub85c \uc804\ub2ec\ud558\ub294 \ubc29\ubc95\uc740 \ud06c\uac8c 2\uac00\uc9c0\uac00 \uc874\uc7ac\ud558\ub294\ub370\uc694. \uac12\uc5d0 \uc758\ud55c \ud638\ucd9c(Call By Value), \ucc38\uc870\uc5d0 \uc758\ud55c \ud638\ucd9c(Call By Reference)\uc774 \uc774\uc5d0 \ud574\ub2f9\ub429\ub2c8\ub2e4.","permalink":"/docs/cs/cs-call-by"},{"id":"db/db-cap-theorem","title":"CAP \uc815\ub9ac (= \ube0c\ub8e8\uc5b4(Brewer)\uc758 \uc815\ub9ac)","description":"CAP \uc815\ub9ac (= \ube0c\ub8e8\uc5b4(Brewer)\uc758 \uc815\ub9ac)\ub294 \ubd84\uc0b0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc774 CAP \uc911 2\uac1c\uc758 \uc18d\uc131\ub9cc\uc744 \uc81c\uacf5\ud560 \uc218 \uc788\ub2e4\ub294 \uc774\ub860\uc785\ub2c8\ub2e4. CAP \uc815\ub9ac\uc5d0 \ub530\ub974\uc790\uba74, \uc77c\uad00\uc131(Consistency), \uac00\uc6a9\uc131(Availability), \ubd84\ud560 \ub0b4\uc131(Partition Tolerance) \ub4f1 3\uac00\uc9c0 \uc18d\uc131\uc744 \ubaa8\ub450 \ub9cc\uc871\ud558\ub294 \ubd84\uc0b0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc740 \uc874\uc7ac\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-cap-theorem"},{"id":"cs/cs-ci-cd-pipeline","title":"CI/CD \ud30c\uc774\ud504\ub77c\uc778","description":"\u2714\ufe0f \uc9c0\uc18d\uc801 \ud1b5\ud569 (Continuous Integration)","permalink":"/docs/cs/cs-ci-cd-pipeline"},{"id":"cs/cs-network-timeout","title":"Connection Timeout, Socket Timeout, Read Timeout\uc758 \ucc28\uc774\uc810","description":"\u2714\ufe0f Connection Timeout","permalink":"/docs/cs/cs-network-timeout"},{"id":"cs/cs-cors","title":"CORS(Cross Origin Resource Sharing)","description":"CORS(Cross Origin Resource Sharing)\ub294 \ucd9c\ucc98\uac00 \ub2e4\ub978 \uacf3\uc758 \ub9ac\uc18c\uc2a4\ub97c \uc694\uccad\ud560 \ub54c \uc811\uadfc \uad8c\ud55c\uc744 \ubd80\uc5ec\ud558\ub294 \uba54\ucee4\ub2c8\uc998\uc785\ub2c8\ub2e4. \ub9ac\uc18c\uc2a4\ub97c \uc8fc\uace0\ubc1b\ub294 \ub450 \uacf3\uc758 \ucd9c\ucc98\uac00 \ub2e4\ub974\uba74 \ucd9c\ucc98\uac00 \uad50\ucc28\ud55c\ub2e4\uace0 \ud569\ub2c8\ub2e4. \uc774\ub54c \ucd9c\ucc98\ub294 URL\ubfd0\ub9cc \uc544\ub2c8\ub77c \ud504\ub85c\ud1a0\ucf5c\uacfc \ud3ec\ud2b8\uae4c\uc9c0 \ud3ec\ud568\ub429\ub2c8\ub2e4. \ub9cc\uc57d \ud074\ub77c\uc774\uc5b8\ud2b8\uc758 \ucd9c\ucc98\uac00 \ud5c8\uc6a9\ub418\uc9c0 \uc54a\uc558\ub2e4\uba74 CORS \uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-cors"},{"id":"cs/cs-cpu-scheduling","title":"CPU \uc2a4\ucf00\uc904\ub9c1","description":"CPU \uc2a4\ucf00\uc904\ub9c1\uc740 \uc6b4\uc601\uccb4\uc81c\uac00 \ud504\ub85c\uc138\uc2a4\ub4e4\uc5d0\uac8c \uacf5\uc815\ud558\uace0 \ud569\ub9ac\uc801\uc73c\ub85c CPU \uc790\uc6d0\uc744 \ubc30\ubd84\ud558\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \ub9cc\uc57d CPU \uc2a4\ucf00\uc904\ub9c1\uc774 \uc5c6\ub2e4\uba74, \ubc18\ub4dc\uc2dc \uc2e4\ud589\ub418\uc5b4\uc57c \ud560 \ud504\ub85c\uc138\uc2a4\ub4e4\uc774 \uc2e4\ud589\ub418\uc9c0 \ubabb\ud560 \uc218 \uc788\uc73c\uba70, \ub2f9\uc7a5 \uae09\ud558\uc9c0 \uc54a\uc740 \ud504\ub85c\uc138\uc2a4\uac00 \uc2e4\ud589\ub418\ub294 \ub4f1 \ubb34\uc9c8\uc11c\ud55c \uc0c1\ud0dc\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. CPU \uc2a4\ucf00\uc904\ub9c1\uc740 \uc120\uc810\ud615\uacfc \ube44\uc120\uc810\ud615 \ubc29\uc2dd\uc73c\ub85c \uad6c\ud604\ud560 \uc218 \uc788\uc73c\uba70, \ub2e4\uc591\ud55c \uc2a4\ucf00\uc904\ub9c1 \uc54c\uace0\ub9ac\uc998\uc774 \uc874\uc7ac\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-cpu-scheduling"},{"id":"cs/cs-cqrs-pattern","title":"CQRS(\uba85\ub839 \ucffc\ub9ac \ucc45\uc784 \ubd84\ub9ac) \ud328\ud134","description":"\uc2dc\uc2a4\ud15c\uc740 \ud06c\uac8c \uc0c1\ud0dc \ubcc0\uacbd\uacfc \uc870\ud68c \uae30\ub2a5\uc744 \uc81c\uacf5\ud558\ub294\ub370\uc694. \uc8fc\ubb38 \ucde8\uc18c, \uacb0\uc81c \uae30\ub2a5\uc740 \uc0c1\ud0dc \ubcc0\uacbd\uc5d0 \ud574\ub2f9\ub418\uba70, \uc8fc\ubb38\uc11c \uc870\ud68c, \uc0ac\uc6a9\uc790 \uc870\ud68c \ub4f1\uc774 \uc870\ud68c\uc5d0 \ud574\ub2f9\ub429\ub2c8\ub2e4.","permalink":"/docs/cs/cs-cqrs-pattern"},{"id":"cs/cs-dns","title":"DNS (Domain Name System)","description":"DNS(Domain Name System) \ub294 \uc6f9\uc0ac\uc774\ud2b8\uc758 \ub3c4\uba54\uc778 \uc8fc\uc18c\ub97c IP \uc8fc\uc18c\ub85c \ubcc0\ud658\ud558\ub294 \uc2dc\uc2a4\ud15c\uc785\ub2c8\ub2e4.","permalink":"/docs/cs/cs-dns"},{"id":"cs/cs-graceful-shutdown","title":"Graceful Shutdown (\uc6b0\uc544\ud55c \uc885\ub8cc)","description":"\u2714\ufe0f Graceful Shutdown (\uc6b0\uc544\ud55c \uc885\ub8cc)","permalink":"/docs/cs/cs-graceful-shutdown"},{"id":"cs/cs-http-https","title":"HTTP vs HTTPS","description":"\u2714\ufe0f HTTP(Hypertext Transfer Protocol)","permalink":"/docs/cs/cs-https"},{"id":"cs/cs-http-method-idempotency","title":"HTTP \uba54\uc11c\ub4dc\uc758 \uba71\ub4f1\uc131","description":"\uc5f0\uc0b0\uc744 \uc5ec\ub7ec \ubc88 \uc801\uc6a9\ud558\ub354\ub77c\ub3c4 \uacb0\uacfc\uac00 \ub2ec\ub77c\uc9c0\uc9c0 \uc54a\ub294 \uc131\uc9c8\uc744 \uba71\ub4f1\uc131\uc774\ub77c\uace0 \ud569\ub2c8\ub2e4. HTTP \uba54\uc11c\ub4dc\uc758 \uba71\ub4f1\uc131\uc740 \ub3d9\uc77c\ud55c \uc694\uccad\uc744 \ud55c\ubc88 \ubcf4\ub0b4\ub294 \uac83\uacfc \uc5ec\ub7ec\ubc88 \ubcf4\ub0b4\ub294 \uac83\uc774 \uc11c\ub85c \ub3d9\uc77c\ud55c \ud6a8\uacfc\ub97c \uc9c0\ub2c8\uba70, \uc11c\ubc84\uc758 \uc0c1\ud0dc\ub3c4 \ub3d9\uc77c\ud558\uac8c \ub0a8\uc744 \uacbd\uc6b0\uc5d0 \uba71\ub4f1\ud558\ub2e4\uace0 \uc774\uc57c\uae30\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub300\ud45c\uc801\uc73c\ub85c \uba71\ub4f1\ud55c \uba54\uc11c\ub4dc\ub294 GET, HEAD, PUT, DELETE, TRACE, OPTIONS \uac00 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-http-method-idempotency"},{"id":"cs/cs-http-version","title":"HTTP/1.1\uacfc HTTP/2.0","description":"\u2714\ufe0f HTTP/1.1","permalink":"/docs/cs/cs-http-version"},{"id":"cs/cs-ip-address-allocation","title":"IP \uc8fc\uc18c \ud560\ub2f9 \ubc29\uc2dd","description":"\ub124\ud2b8\uc6cc\ud06c\uc5d0\uc11c \ud638\uc2a4\ud2b8\uc5d0\uac8c IP\ub97c \ud560\ub2f9\ud558\ub294 \ubc29\uc2dd\uc740 \ud06c\uac8c \uc815\uc801 \ud560\ub2f9 \ubc29\uc2dd\uacfc \ub3d9\uc801 \ud560\ub2f9 \ubc29\uc2dd\uc774 \uc874\uc7ac\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-ip-address-allocation"},{"id":"cs/cs-jwt","title":"JWT (Json Web Token)","description":"JWT(Json Web Token) \uc740 \ud1b5\uc2e0 \uc815\ubcf4\ub97c JSON \ud615\uc2dd\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc548\uc804\ud558\uac8c \uc804\uc1a1\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub429\ub2c8\ub2e4. JWT\ub294 \ud1a0\ud070 \uc790\uccb4\uc5d0 \uc815\ubcf4\uac00 \ud3ec\ud568\ub418\uc5b4 \uc788\ub294 Claim \uae30\ubc18 \ud1a0\ud070\uc785\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc778 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c JWT\ub294 \uc8fc\ub85c \uc778\uc99d\uacfc \uc778\uac00\ub97c \uad6c\ud604\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub429\ub2c8\ub2e4. JWT\ub294 \ud5e4\ub354, \ud398\uc774\ub85c\ub4dc, \uc2dc\uadf8\ub2c8\ucc98\ub85c \uad6c\ubd84\ub429\ub2c8\ub2e4. \ud5e4\ub354\uc5d0\ub294 \ud1a0\ud070\uc758 \uc554\ud638\ud654 \uc54c\uace0\ub9ac\uc998\uc774\ub098 \ud0c0\uc785\uc744 \uac00\uc9c0\uba70, \ud398\uc774\ub85c\ub4dc\uc5d0\ub294 \ub370\uc774\ud130(\ub9cc\ub8cc\uc77c, \uc0ac\uc6a9\uc790 \uc815\ubcf4 \ub4f1)\ub97c \uac00\uc9d1\ub2c8\ub2e4. \uc2dc\uadf8\ub2c8\ucc98\ub294 \ud5e4\ub354\uc640 \ud398\uc774\ub85c\ub4dc\uac00 \ubcc0\uc870\ub418\uc9c0 \uc54a\uc558\ub294\uc9c0 \ud310\ub2e8\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub418\ub294\ub370\uc694. \ud5e4\ub354\uc640 \ud398\uc774\ub85c\ub4dc\ub97c \ube44\ubc00 \ud0a4\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud5e4\ub354\uc5d0 \uba85\uc2dc\ub41c \uc554\ud638\ud654 \uc54c\uace0\ub9ac\uc998\uc73c\ub85c \uc554\ud638\ud654\ud558\uc5ec \uc2dc\uadf8\ub2c8\ucc98\uac00 \ub9cc\ub4e4\uc5b4\uc9d1\ub2c8\ub2e4.","permalink":"/docs/cs/cs-jwt"},{"id":"cs/cs-nat","title":"NAT (Network Address Translation)","description":"IP \uc8fc\uc18c\ub294 \uacf5\uc778 IP \uc8fc\uc18c(Public IP Address) \uc640 \uc0ac\uc124 IP \uc8fc\uc18c(Private IP Address) \uac00 \uc874\uc7ac\ud569\ub2c8\ub2e4. \uacf5\uc778 IP \uc8fc\uc18c\ub294 \uace0\uc720\ud558\uba70, \uc0ac\uc124 IP \uc8fc\uc18c\ub294 \uace0\uc720\ud558\uc9c0 \uc54a\uace0 \ud2b9\uc815 \uc0ac\uc124 \ub124\ud2b8\uc6cc\ud06c\uc5d0\uc11c\ub9cc \uc0ac\uc6a9\ub429\ub2c8\ub2e4. \uc774\ub54c, \uc0ac\uc124 \ub124\ud2b8\uc6cc\ud06c\ub294 \uc678\ubd80 \ub124\ud2b8\uc6cc\ud06c\uc5d0 \uacf5\uac1c\ub418\uc9c0 \uc54a\uc740 \ub124\ud2b8\uc6cc\ud06c\ub97c \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc0ac\uc124 IP\ub294 \uc77c\ubc18\uc801\uc73c\ub85c \ub77c\uc6b0\ud130\uac00 \ud560\ub2f9\ud558\uba70, \ud560\ub2f9\ubc1b\uc740 \uc0ac\uc124 IP \uc8fc\uc18c\ub294 \uc0ac\uc124 \ub124\ud2b8\uc6cc\ud06c \uc0c1\uc5d0\uc11c\ub9cc \uc720\ud6a8\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-nat"},{"id":"cs/cs-prg-pattern","title":"PRG(Post/Redirect/Get) \ud328\ud134","description":"\u2714\ufe0f PRG \ud328\ud134","permalink":"/docs/cs/cs-prg-pattern"},{"id":"cs/cs-rest","title":"REST\ub780?","description":"\u2714\ufe0f REST(Representational State Transfer)","permalink":"/docs/cs/cs-rest"},{"id":"cs/cs-tcp-3-way-handshake","title":"TCP 3-way handshake","description":"3-way handshake","permalink":"/docs/cs/cs-tcp-3-way-handshake"},{"id":"cs/cs-uri-url-urn","title":"URI, URL, URN\uc758 \ucc28\uc774\uc810","description":"URI, URL, URN","permalink":"/docs/cs/cs-uri-url-urn"},{"id":"cs/cs-virtualization","title":"\uac00\uc0c1\ud654(Virtualization)","description":"\u2714\ufe0f \uac00\uc0c1\ud654 (Virtualization)","permalink":"/docs/cs/cs-virtualization"},{"id":"cs/cs-solid-principle","title":"\uac1d\uccb4\uc9c0\ud5a5 \uc124\uacc4 5\uc6d0\uce59(SOLID)","description":"SOLID \uc6d0\uce59\uc740 \uac1d\uccb4\uc9c0\ud5a5 \uc124\uacc4 5\uc6d0\uce59\uc774\ub77c\uace0\ub3c4 \ubd88\ub9ac\uba70, \uac01 \uc6d0\uce59\uc758 \uc55e \uae00\uc790\ub97c \ub530\uc11c \ub9cc\ub4e4\uc5b4\uc84c\uc2b5\ub2c8\ub2e4. \uac1d\uccb4\uc9c0\ud5a5\uc124\uacc4\uc758 \ud575\uc2ec \uc911 \ud558\ub098\ub294 \uc758\uc874\uc131\uc744 \uad00\ub9ac\ud558\ub294 \uac83\uc778\ub370\uc694. \uc758\uc874\uc131\uc744 \uc798 \uad00\ub9ac\ud558\uae30 \uc704\ud574\uc11c\ub294 SOLID \uc6d0\uce59\uc744 \uc900\uc218\ud574\uc57c \ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-solid-principle"},{"id":"cs/cs-race-condition","title":"\uacbd\uc7c1 \uc0c1\ud0dc (Race Condition)","description":"\uacbd\uc7c1 \uc0c1\ud0dc(Race Condition) \ub294 \ub450 \uac1c \uc774\uc0c1\uc758 \uc2a4\ub808\ub4dc\uac00 \uacf5\uc720 \uc790\uc6d0\uc5d0 \ub3d9\uc2dc\uc5d0 \uc811\uadfc\ud560 \ub54c \uc2a4\ub808\ub4dc \uac04\uc758 \uc2e4\ud589 \uc21c\uc11c\uc5d0 \ub530\ub77c \uacb0\uacfc\uac00 \ub2ec\ub77c\uc9c0\ub294 \ud604\uc0c1\uc73c\ub85c, \uc6d0\uc790\uc131\uacfc \uac00\uc2dc\uc131 \ubaa8\ub450 \ubcf4\uc7a5\ub418\uc5b4\uc57c \ud574\uacb0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-race-condition"},{"id":"cs/cs-deadlock","title":"\uad50\ucc29 \uc0c1\ud0dc(deadlock)","description":"\uad50\ucc29 \uc0c1\ud0dc(deadlock) \ub294 \ub450 \uac1c \uc774\uc0c1\uc758 \uc791\uc5c5\uc774 \uc11c\ub85c \uc0c1\ub300\ubc29\uc758 \uc791\uc5c5\uc774 \ub05d\ub098\uae30\ub9cc\uc744 \uae30\ub2e4\ub9ac\uace0 \uc788\uc5b4 \uacb0\uacfc\uc801\uc73c\ub85c \uc544\ubb34\uac83\ub3c4 \uc644\ub8cc\ub418\uc9c0 \ubabb\ud558\ub294 \uc0c1\ud0dc\ub97c \uc758\ubbf8\ud569\ub2c8\ub2e4. \uac00\ub839, A \ud504\ub85c\uc138\uc2a4\uac00 \uc790\uc6d0 A\ub97c \uac00\uc9c0\uace0 \uc790\uc6d0 B\ub97c \ud544\uc694\ub85c \ud569\ub2c8\ub2e4. \uadf8\ub9ac\uace0, B \ud504\ub85c\uc138\uc2a4\ub294 \uc790\uc6d0 B\ub97c \uac00\uc9c0\uace0 \uc790\uc6d0 A\uac00 \ud544\uc694\ud560 \ub54c \ub450 \uac1c\uc758 \ud504\ub85c\uc138\uc2a4\ub294 \uad50\ucc29 \uc0c1\ud0dc\uc5d0 \ube60\uc838 \uc5b4\ub290 \uc791\uc5c5\ub3c4 \uc9c4\ud589\ud560 \uc218 \uc5c6\ub294 \uc0c1\ud669\uc774 \ub429\ub2c8\ub2e4.","permalink":"/docs/cs/cs-deadlock"},{"id":"cs/cs-ip-addressing","title":"\ub124\ud2b8\uc6cc\ud06c IP \uc8fc\uc18c \uccb4\uacc4","description":"\u2714\ufe0f \ud074\ub798\uc2a4\ud480 \uc8fc\uc18c \uccb4\uacc4 (Classful Addressing)","permalink":"/docs/cs/cs-ip-addressing"},{"id":"cs/cs-keep-alive","title":"\ub124\ud2b8\uc6cc\ud06c Keep Alive \uc18d\uc131","description":"Keep Alive\ub294 \ub124\ud2b8\uc6cc\ud06c \ub610\ub294 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ucee4\ub125\uc158\uc744 \uc9c0\uc18d\ud574\uc11c \uc720\uc9c0\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub418\ub294 \uae30\uc220\uc774\ub098 \uc124\uc815\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-keep-alive"},{"id":"cs/cs-session-based-authentication","title":"\ub2e4\uc911 \uc11c\ubc84 \ud658\uacbd\uc758 \uc138\uc158 \uae30\ubc18 \uc778\uc99d \ubc29\uc2dd","description":"\ub2e4\uc911 \uc11c\ubc84 \ud658\uacbd\uc5d0\uc11c \uc138\uc158 \uae30\ubc18 \uc778\uc99d \ubc29\uc2dd\uc744 \uc0ac\uc6a9\ud558\ub294 \uacbd\uc6b0\uc5d0\ub294 \uc138\uc158 \ubd88\uc77c\uce58 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub9cc\uc57d \uc11c\ubc84 A, B\ub97c \uad00\ub9ac\ud558\uace0 \uc788\uc744 \ub54c, \ub85c\ub4dc\ubc38\ub7f0\uc11c\ub294 \uc0ac\uc6a9\uc790\uc758 \uc694\uccad\uc744 \uc0c1\ud669\uc5d0 \ub9de\uac8c A, B \uc911 \ud55c \uacf3\uc73c\ub85c \uc804\ub2ec\ud569\ub2c8\ub2e4. \uc720\ud6a8\ud55c \ub85c\uadf8\uc778 \uc694\uccad\uc774 A \uc11c\ubc84\ub85c \ucc98\uc74c \ub3c4\ucc29\ud558\uba74 \uc0ac\uc6a9\uc790\uc5d0 \ub300\ud55c \uc138\uc158 \uc815\ubcf4\ub294 A \uc11c\ubc84\uc5d0 \uc800\uc7a5\ub429\ub2c8\ub2e4. \uc774\ud6c4\uc5d0 \ud574\ub2f9 \uc0ac\uc6a9\uc790\uc758 \ub610 \ub2e4\ub978 \uc694\uccad\uc774 \ub85c\ub4dc \ubc38\ub7f0\uc11c\uc5d0 \ub3c4\ucc29\ud588\uc744 \ub54c, B \uc11c\ubc84\ub85c \ub3c4\ucc29\ud558\uac8c \ub418\uba74 \uc0ac\uc6a9\uc790\uc758 \uc138\uc158 \ub370\uc774\ud130\uac00 \uc874\uc7ac\ud558\uc9c0 \uc54a\uae30 \ub54c\ubb38\uc5d0 \uc694\uccad\uc774 \uc81c\ub300\ub85c \ucc98\ub9ac\ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc774\ub97c \uc138\uc158 \ubd88\uc77c\uce58 \ubb38\uc81c\ub77c\uace0 \ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-session-based-authentication"},{"id":"cs/cs-unit-integration-test","title":"\ub2e8\uc704 \ud14c\uc2a4\ud2b8\uc640 \ud1b5\ud569 \ud14c\uc2a4\ud2b8","description":"\ub2e8\uc704 \ud14c\uc2a4\ud2b8","permalink":"/docs/cs/cs-unit-integration-test"},{"id":"cs/cs-single-process-system","title":"\ub2e8\uc77c \ud504\ub85c\uc138\uc2a4 \uc2dc\uc2a4\ud15c","description":"\u2714\ufe0f \ub2e8\uc77c \ud504\ub85c\uc138\uc2a4","permalink":"/docs/cs/cs-single-process-system"},{"id":"cs/cs-sync-async-blocking-non-blocking","title":"\ub3d9\uae30 vs \ube44\ub3d9\uae30, \ube14\ub85c\ud0b9 vs \ub17c\ube14\ub85c\ud0b9","description":"\ud638\ucd9c\ud558\ub294 \ud568\uc218\uc758 \uc791\uc5c5 \uc644\ub8cc\ub97c \uae30\ub2e4\ub9ac\ub294\uc9c0 \uc5ec\ubd80\uc758 \ucc28\uc774\uac00 \uc788\uc2b5\ub2c8\ub2e4. \ud568\uc218 A\uac00 \ub3d9\uae30\ub85c \ud568\uc218 B\ub97c \ud638\ucd9c\ud558\uba74 A\ub294 B\uc758 \uc791\uc5c5\uc774 \uc644\ub8cc\ub420 \ub54c\uae4c\uc9c0 \uae30\ub2e4\ub824\uc57c \ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \uc791\uc5c5\uc774 \uc21c\ucc28\uc801\uc73c\ub85c \uc9c4\ud589\ub429\ub2c8\ub2e4. \ubc18\uba74, \ud568\uc218 A\uac00 \ube44\ub3d9\uae30\ub85c \ud568\uc218 B\ub97c \ud638\ucd9c\ud558\uba74 A\ub294 B\uc758 \uc791\uc5c5 \uc644\ub8cc\ub97c \uc2e0\uacbd \uc4f0\uc9c0 \uc54a\uace0 \ub530\ub85c \ub3d9\uc791\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \uc791\uc5c5\uc774 \uc21c\ucc28\uc801\uc73c\ub85c \uc9c4\ud589\ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-sync-async-blocking-non-blocking"},{"id":"cs/cs-concurrency-and-parallelism","title":"\ub3d9\uc2dc\uc131\uacfc \ubcd1\ub82c\uc131","description":"Concurrency and Parallelism","permalink":"/docs/cs/cs-concurrency-and-parallelism"},{"id":"cs/cs-disk-access-time","title":"\ub514\uc2a4\ud06c \uc811\uadfc \uc2dc\uac04(Disk Access Time)","description":"\uc77c\ubc18\uc801\uc73c\ub85c \ub2e8\uc77c-\ud5e4\ub4dc \ub514\uc2a4\ud06c \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ud2b9\uc815 \ub370\uc774\ud130 \ube14\ub85d(\ud558\ub098 \uc774\uc0c1\uc758 \uc139\ud130\ub85c \uc774\ub8e8\uc5b4\uc9d0)\uc744 \uc77d\uac70\ub098 \uc4f0\uae30 \uc704\ud574\uc11c\ub294","permalink":"/docs/cs/cs-disk-access-time"},{"id":"cs/cs-log-and-metric","title":"\ub85c\uadf8\uc640 \uba54\ud2b8\ub9ad","description":"\u2714\ufe0f \ub85c\uadf8","permalink":"/docs/cs/cs-log-and-metric"},{"id":"cs/cs-load-balancing","title":"\ub85c\ub4dc \ubc38\ub7f0\uc2f1","description":"\ub85c\ub4dc \ubc38\ub7f0\uc2f1\uc774\ub780 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uc9c0\uc6d0\ud558\ub294 \ub9ac\uc18c\uc2a4 \ud480\uc5d0 \ub4e4\uc5b4\uc624\ub294 \ub124\ud2b8\uc6cc\ud06c \ud2b8\ub798\ud53d(\ub4e4\uc5b4\uc624\ub294 \uc694\uccad)\uc744 \uade0\ub4f1\ud558\uac8c \ubd84\uc0b0\ud558\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc774\ub97c \uc218\ud589\ud558\ub294 \ub85c\ub4dc \ubc38\ub7f0\uc11c\ub294 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc11c\ubc84 \uc55e\ub2e8\uc5d0 \uc704\uce58\ud558\uba70 \ud074\ub77c\uc774\uc5b8\ud2b8 \uc694\uccad\uc744 \uc9c0\uc2dc\ud558\uace0 \uc81c\uc5b4\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574\uc11c \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc758 \uac00\uc6a9\uc131, \ud655\uc7a5\uc131, \ubcf4\uc548 \ubc0f \uc131\ub2a5\uc744 \ud655\ubcf4\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-load-balancing"},{"id":"cs/cs-proxy","title":"\ub9ac\ubc84\uc2a4 \ud504\ub85d\uc2dc\uc640 \ud3ec\uc6cc\ub4dc \ud504\ub85d\uc2dc\uc758 \ucc28\uc774\uc810","description":"\u2714\ufe0f \ud3ec\uc6cc\ub4dc \ud504\ub85d\uc2dc(Forward Proxy)","permalink":"/docs/cs/cs-proxy"},{"id":"cs/cs-multi-tasking-system","title":"\uba40\ud2f0 \ud0dc\uc2a4\ud0b9 \uc2dc\uc2a4\ud15c\uc758 \ud55c\uacc4","description":"\u2714\ufe0f \uba40\ud2f0 \ud0dc\uc2a4\ud0b9(Multi-Tasking) \uc2dc\uc2a4\ud15c\uc758 \ubb38\uc81c","permalink":"/docs/cs/cs-multi-tasking-system"},{"id":"cs/cs-zero-downtime-deployment","title":"\ubb34\uc911\ub2e8 \ubc30\ud3ec (Zero-Downtime Deployment)","description":"\ubb34\uc911\ub2e8 \ubc30\ud3ec(Zero-Downtime Deployment) \ub294 \uc11c\ube44\uc2a4\uc5d0 \ub2e4\uc6b4 \ud0c0\uc784\uc774 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c, \uc0c8\ub85c\uc6b4 \ubc84\uc804\uc758 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uc11c\ubc84\uc5d0 \ubc30\ud3ec\ud558\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \ubb34\uc911\ub2e8 \ubc30\ud3ec \ud328\ud134\uc5d0\ub294 \ub300\ud45c\uc801\uc73c\ub85c \uc21c\ucc28\uc801\uc73c\ub85c \ubc30\ud3ec\ud558\ub294 \ub864\ub9c1 \ubc30\ud3ec, \uc804\uccb4 \uc11c\ubc84\ub97c \ud1b5\uc9f8\ub85c \ubc14\uafb8\ub294 \ube14\ub8e8/\uadf8\ub9b0 \ubc30\ud3ec, \ud2b8\ub798\ud53d\uc744 \uc21c\ucc28\uc801\uc73c\ub85c \uc774\ub3d9\uc2dc\ud0a4\ub294 \uce74\ub098\ub9ac \ubc30\ud3ec\uac00 \uc874\uc7ac\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-zero-downtime-deployment"},{"id":"cs/cs-csrf","title":"\uc0ac\uc774\ud2b8 \uac04 \uc694\uccad \uc704\uc870(Cross-site Request Forgery, CSRF) \uacf5\uaca9","description":"\uc0ac\uc774\ud2b8 \uac04 \uc694\uccad \uc704\uc870(Cross-site Request Forgery, CSRF) \uacf5\uaca9\uc740 \uc0ac\uc6a9\uc790\uac00 \uc790\uc2e0\uc758 \uc758\uc9c0\uc640 \uc0c1\uad00\uc5c6\uc774 \uacf5\uaca9\uc790\uac00 \uc758\ub3c4\ud55c \ud589\uc704\ub97c \ud2b9\uc815 \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc694\uccad\ud558\ub3c4\ub85d \ud558\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-csrf"},{"id":"cs/cs-ssr-and-csr","title":"\uc11c\ubc84 \uc0ac\uc774\ub4dc \ub80c\ub354\ub9c1(SSR)\uacfc \ud074\ub77c\uc774\uc5b8\ud2b8 \uc0ac\uc774\ub4dc \ub80c\ub354\ub9c1(CSR)","description":"\u2714\ufe0f \uc11c\ubc84 \uc0ac\uc774\ub4dc \ub80c\ub354\ub9c1(SSR)","permalink":"/docs/cs/cs-ssr-and-csr"},{"id":"cs/cs-computing-resources","title":"\uc2a4\ub808\ub4dc, \ud504\ub85c\uc138\uc2a4, \ucf54\uc5b4\uc758 \uc218\ub294 \ub9ce\uc744 \uc218\ub85d \uc88b\uc744\uae4c?","description":"\uc2a4\ub808\ub4dc, \ud504\ub85c\uc138\uc2a4, \ucf54\uc5b4\uc758 \uc218\uac00 \ub9ce\uc744\uc218\ub85d \uc2dc\uc2a4\ud15c \uc131\ub2a5\uc774 \ud5a5\uc0c1\ub41c\ub2e4\uace0 \uc0dd\uac01\ud560 \uc218 \uc788\uc9c0\ub9cc, \uc2e4\uc81c\ub85c\ub294 \uadf8\ub807\uc9c0 \uc54a\uc744 \ud655\ub960\uc774 \ud07d\ub2c8\ub2e4.","permalink":"/docs/cs/cs-computing-resources"},{"id":"cs/cs-scale-up-out","title":"\uc2a4\ucf00\uc77c \uc544\uc6c3\uacfc \uc2a4\ucf00\uc77c \uc5c5","description":"\uae30\uc874 \uac1c\ubc1c\ud558\uace0 \uc788\ub358 \uc11c\ube44\uc2a4\uc758 \uc11c\ubc84\uac00 \ud55c\uacc4\uc5d0 \ub3c4\ub2ec\ud558\ub294 \uacbd\uc6b0, \uc2a4\ucf00\uc77c \uc5c5(Scale-Up) \ud639\uc740 \uc2a4\ucf00\uc77c \uc544\uc6c3(Scale-Out) \uc744 \uace0\ub824\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-scale-up-out"},{"id":"cs/cs-stack-queue","title":"\uc2a4\ud0dd(Stack) \uc790\ub8cc \uad6c\uc870","description":"\u2714\ufe0f \uc2a4\ud0dd (Stack)","permalink":"/docs/cs/cs-stack-queue"},{"id":"cs/cs-async-integration","title":"\uc2dc\uc2a4\ud15c \uac04 \ube44\ub3d9\uae30 \uc5f0\ub3d9 \ubc29\uc2dd","description":"\ubd84\ub9ac\ub41c \uc2dc\uc2a4\ud15c \uac04\uc758 \ube44\ub3d9\uae30 \uc5f0\ub3d9\uc740 \uc2dc\uc2a4\ud15c \uac04\uc758 \uacb0\ud569\ub3c4\ub97c \ub0ae\ucd9c \uc218 \uc788\uc73c\uba70, \ud638\ucd9c\ub41c \uc2dc\uc2a4\ud15c\uc758 \uc751\ub2f5\uc744 \uae30\ub2e4\ub9ac\uc9c0 \uc54a\uc73c\ubbc0\ub85c \ub354\uc6b1 \ube68\ub9ac \uc0ac\uc6a9\uc790\uc758 \uc694\uccad\uc5d0 \uc751\ub2f5\ud560 \uc218 \uc788\ub2e4\ub294 \uc7a5\uc810\uc774 \uc788\uc2b5\ub2c8\ub2e4. \ube44\ub3d9\uae30 \uc5f0\ub3d9 \ubc29\uc2dd\uc73c\ub85c \uba54\uc2dc\uc9d5 \uc2dc\uc2a4\ud15c \ud65c\uc6a9, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud65c\uc6a9, CDC \ud65c\uc6a9 \ubc29\uc2dd\uc774 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-async-integration"},{"id":"cs/cs-system-call","title":"\uc2dc\uc2a4\ud15c \ucf5c (System Call)","description":"\uc6b4\uc601\uccb4\uc81c\ub294 \uc0ac\uc6a9\uc790\uac00 \uc2e4\ud589\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc774 \ud558\ub4dc\uc6e8\uc5b4 \uc790\uc6d0\uc5d0 \uc9c1\uc811 \uc811\uadfc\ud558\ub294 \uac83\uc744 \ubc29\uc9c0\ud574 \uc790\uc6d0\uc744 \ubcf4\ud638\ud569\ub2c8\ub2e4. \uc65c\ub0d0\ud558\uba74, \ud504\ub85c\uadf8\ub7a8\uc774 CPU, \uba54\ubaa8\ub9ac, \ud558\ub4dc \ub514\uc2a4\ud06c\uc5d0 \ub9c8\uc74c\ub300\ub85c \uc811\uadfc\ud558\uace0 \uc870\uc791\ud560 \uc218 \uc788\ub2e4\uba74, \uc790\uc6d0\uc774 \ubb34\uc9c8\uc11c\ud558\uac8c \uad00\ub9ac \ub420 \uc218 \uc788\uc73c\uba70 \ud55c \ud504\ub85c\uadf8\ub7a8\uc758 \uc2e4\uc218\uac00 \uc804\uccb4 \ucef4\ud4e8\ud130\uc5d0 \uc601\ud5a5\uc744 \uc8fc\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. \uc6b4\uc601\uccb4\uc81c\ub294 \ud504\ub85c\uadf8\ub7a8\ub4e4\uc774 \uc790\uc6d0\uc5d0 \uc811\uadfc\ud558\ub824 \ud560 \ub54c \uc624\uc9c1 \uc790\uc2e0\uc744 \ud1b5\ud574\uc11c\ub9cc \uc811\uadfc\ud558\ub3c4\ub85d \ud558\uc5ec \uc790\uc6d0\uc744 \ubcf4\ud638\ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-system-call"},{"id":"cs/cs-algorithm-complexity","title":"\uc54c\uace0\ub9ac\uc998 \ubcf5\uc7a1\ub3c4 \uce21\uc815","description":"\ud558\ub098\uc758 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \uc5ec\ub7ec \uc54c\uace0\ub9ac\uc998\uc774 \uc874\uc7ac\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0, \uac1c\ubc1c\uc790\ub294 \uc131\ub2a5\uc744 \ud3c9\uac00\ud558\uc5ec \ud558\ub098\ub97c \uacb0\uc815\ud574\uc57c \ud569\ub2c8\ub2e4. \uc774\ub54c, \ucf54\ub4dc\uac00 \uc2e4\ud589\ub420 \ub54c \uac78\ub9ac\ub294 \uc815\ud655\ud55c \uc2dc\uac04\uc744 \uce21\uc815\ud558\ub294 \ubc29\ubc95\uc73c\ub85c \uc18d\ub3c4\ub97c \ube44\uad50\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud558\uc9c0\ub9cc, \uc2e4\ud589 \uc2dc\uac04\uc740 \uae30\uacc4\uc5d0 \uc758\uc874\uc801\uc774\uba70 \ub300\uc548\uc73c\ub85c \ub098\uc628 \uc54c\uace0\ub9ac\uc998\ub4e4\uc774 \ubaa8\ub450 \uc9e7\uc740 \uc2dc\uac04 \ub0b4\ub85c \uc218\ud589\ub418\uc5b4 \ube44\uad50\uac00 \uc5b4\ub824\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-algorithm-complexity"},{"id":"cs/cs-continuous-memory-allocation","title":"\uc5f0\uc18d \uba54\ubaa8\ub9ac \ud560\ub2f9 \uae30\ubc95 (Continuous Memory Allocation)","description":"\uc5f0\uc18d \uba54\ubaa8\ub9ac \ud560\ub2f9 \uae30\ubc95(Continuous Memory Allocation) \uc740 \uc6b4\uc601\uccb4\uc81c\uac00 \ud504\ub85c\uc138\uc2a4\uc5d0 \uc5f0\uc18d\uc801\uc778 \uba54\ubaa8\ub9ac \uacf5\uac04\uc744 \ud560\ub2f9\ud558\ub294 \ubc29\ubc95\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc5f0\uc18d \uba54\ubaa8\ub9ac \ud560\ub2f9 \uae30\ubc95\uc744 \uc0ac\uc6a9\ud558\uba74, \ud558\ub098\uc758 \ud504\ub85c\uc138\uc2a4\ub294 \uba54\ubaa8\ub9ac \uc8fc\uc18c \uacf5\uac04\uc5d0\uc11c \uc5f0\uc18d\uc801\uc73c\ub85c \uc874\uc7ac\ud558\uac8c \ub429\ub2c8\ub2e4.","permalink":"/docs/cs/cs-continuous-memory-allocation"},{"id":"cs/cs-third-party-sync-failure","title":"\uc678\ubd80 API \uc7a5\uc560 \ub300\uc751","description":"\uc678\ubd80 \uc11c\ube44\uc2a4 \uc7a5\uc560\ub85c \uc778\ud574 \uc751\ub2f5\uc774 \uc624\ub798 \uac78\ub9b0\ub2e4\uace0 \ud588\uc744 \ub54c, \uc678\ubd80 API \uc751\ub2f5\uc73c\ub85c \ub300\uae30\ud558\ub294 \uc790\uc6d0\ub4e4\uc774 \uc6b4\uc601 \uc11c\ubc84 \ub0b4\ubd80\uc5d0 \uc313\uc774\uba74\uc11c \uc131\ub2a5\uc5d0 \uc545\uc601\ud5a5\uc744 \uc904 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud55c \uac00\uc7a5 \uae30\ubcf8\uc801\uc778 \ubc29\ubc95\uc740 \ud0c0\uc784\uc544\uc6c3\uc744 \uc124\uc815\ud558\ub294 \uac83\uc785\ub2c8\ub2e4. \ud06c\uac8c \ud0c0\uc784\uc544\uc6c3\uc5d0\ub294 \ucee4\ub125\uc158 \ud0c0\uc784\uc544\uc6c3\uacfc \ub9ac\ub4dc \ud0c0\uc784\uc544\uc6c3, HTTP \ucee4\ub125\uc158 \ud480 \ud0c0\uc784\uc544\uc6c3\uc744 \uc124\uc815\ud574 \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-third-party-sync-failure"},{"id":"cs/cs-web-process","title":"\uc6f9 \ube0c\ub77c\uc6b0\uc800\uc5d0 URL \uc785\ub825\ud558\uba74 \uc77c\uc5b4\ub098\ub294 \uc77c","description":"\uc608\ub97c \ub4e4\uc5b4 \uc0ac\uc6a9\uc790\uac00 www.google.com\uc744 \uc785\ub825\ud558\uba74, \ube0c\ub77c\uc6b0\uc800\ub294 HTTP \ud504\ub85c\ud1a0\ucf5c\uc744 \uc0ac\uc6a9\ud574 \uad6c\uae00 \uc6f9 \uc11c\ubc84\uc640 \ud1b5\uc2e0\ud558\ub824\uace0 \ud569\ub2c8\ub2e4. HTTP\ub294 OSI 7\uacc4\uce35 \uc911 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uacc4\uce35\uc5d0\uc11c \ub3d9\uc791\ud558\ub294 \ud504\ub85c\ud1a0\ucf5c\uc785\ub2c8\ub2e4.","permalink":"/docs/cs/cs-web-process"},{"id":"cs/cs-ws-vs-was","title":"\uc6f9 \uc11c\ubc84\uc640 WAS(Web Application Server)","description":"\u2714\ufe0f \uc6f9 \uc11c\ubc84 (Web Server)","permalink":"/docs/cs/cs-ws-vs-was"},{"id":"cs/cs-cohesion-and-coupling","title":"\uc751\uc9d1\ub3c4\uc640 \uacb0\ud569\ub3c4","description":"\u2714\ufe0f \uc751\uc9d1\ub3c4(Cohesion)","permalink":"/docs/cs/cs-cohesion-and-coupling"},{"id":"cs/cs-first-class-collection","title":"\uc77c\uae09 \uceec\ub809\uc158","description":"\ud558\ub098\uc758 \uceec\ub809\uc158\uc744 \uac10\uc2f8\ub294 \ud074\ub798\uc2a4\ub97c \ub9cc\ub4e4\uace0, \ud574\ub2f9 \ud074\ub798\uc2a4\uc5d0\uc11c \uceec\ub809\uc158\uacfc \uad00\ub828\ub41c \ube44\uc988\ub2c8\uc2a4 \ub85c\uc9c1\uc744 \uad00\ub9ac\ud558\ub294 \ud328\ud134\uc744 \ub9d0\ud569\ub2c8\ub2e4. \uc544\ub798 \ucf54\ub4dc \uc911\uc5d0\uc11c Order\uc758 List \uc790\ub8cc\uad6c\uc870\ub97c \uac10\uc2fc Orders\uac00 \uc77c\uae09 \uceec\ub809\uc158\uc758 \uc608\uc2dc\uc785\ub2c8\ub2e4.","permalink":"/docs/cs/cs-first-class-collection"},{"id":"cs/cs-strategy-pattern","title":"\uc804\ub7b5 \ud328\ud134 (Strategy Pattern)","description":"\uc804\ub7b5 \ud328\ud134(Strategy Pattern)\uc740 \uac1d\uccb4\uc758 \ud589\uc704\ub97c \ub3d9\uc801\uc73c\ub85c \ubcc0\uacbd\ud558\uace0 \uc2f6\uc740 \uacbd\uc6b0, \ucf54\ub4dc\ub97c \uc9c1\uc811 \uc218\uc815\ud558\ub294 \uac83\uc774 \uc544\ub2cc \ucd94\uc0c1\ud654\ub41c \uc804\ub7b5\uc758 \uad6c\ud604\ub9cc\uc744 \ubc14\uafd4 \uac1d\uccb4\uc758 \ud589\uc704\ub97c \ubcc0\uacbd\ud558\ub294 \ub514\uc790\uc778 \ud328\ud134\uc785\ub2c8\ub2e4. \uc790\ubc14 \uc5b8\uc5b4\uc758 \uc694\uc18c\uc640 \ud568\uaed8 \uc124\uba85\ud574 \ub4dc\ub9ac\uc790\uba74, \uac1d\uccb4\uc758 \ud589\uc704\ub97c Interface\ub85c \uc815\uc758\ud558\uace0, Interface\uc758 \uba54\uc11c\ub4dc\ub97c \uad6c\ud604\ud558\ub294 \uad6c\ud604\uccb4\ub4e4\uc744 \uc8fc\uc785\ud558\ub294 \uac83\uc774 \uc804\ub7b5 \ud328\ud134\uc758 \ub300\ud45c\uc801\uc778 \ud615\ud0dc\uc785\ub2c8\ub2e4.","permalink":"/docs/cs/cs-strategy-pattern"},{"id":"cs/cs-cdn","title":"\ucee8\ud150\uce20 \uc804\uc1a1 \ub124\ud2b8\uc6cc\ud06c (Content Delivery Network, CDN)","description":"\ucee8\ud150\uce20 \uc804\uc1a1 \ub124\ud2b8\uc6cc\ud06c(Content Delivery Network, CDN) \uc740 \uc804 \uc138\uacc4\uc5d0 \ubd84\uc0b0\ub41c \uc11c\ubc84 \ub124\ud2b8\uc6cc\ud06c\uba70, \uc0ac\uc6a9\uc790\uc640 \ubb3c\ub9ac\uc801\uc73c\ub85c \uac00\uae4c\uc6b4 \uc704\uce58\uc5d0\uc11c \uc815\uc801 \ucee8\ud150\uce20(\ud639\uc740 \ub3d9\uc801 \ucee8\ud150\uce20)\ub97c \uc81c\uacf5\ud558\uc5ec \uc11c\ubc84 \uacfc\ubd80\ud558\ub97c \ubc29\uc9c0\ud558\uace0 \ud1b5\uc2e0 \uc9c0\uc5f0\uc744 \ub2e8\ucd95\ud569\ub2c8\ub2e4. CDN\uc740 \ud1b5\uc2e0\uc5d0 \ucc38\uc5ec\ud558\ub294 \ud638\uc2a4\ud2b8 \uac04\uc5d0 \uc911\uac04 \uc11c\ubc84\ub97c \ub450\uc5b4 \uc131\ub2a5\uc744 \ud5a5\uc0c1\ud569\ub2c8\ub2e4. \ub300\ud45c\uc801\uc778 CDN \uc11c\ube44\uc2a4\ub85c\ub294 CloudFront, CloudFlare \ub4f1\uc774 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-cdn"},{"id":"cs/cs-code-coverage","title":"\ucf54\ub4dc \ucee4\ubc84\ub9ac\uc9c0 (Code Coverage)","description":"\u2714\ufe0f \ucf54\ub4dc \ucee4\ubc84\ub9ac\uc9c0 (Code Coverage)","permalink":"/docs/cs/cs-code-coverage"},{"id":"cs/cs-infrastructure-as-code","title":"\ucf54\ub4dc\ud615 \uc778\ud504\ub77c (Infrastructure as Code, IaC)","description":"\ucf54\ub4dc\ud615 \uc778\ud504\ub77c(Infrastructure as Code, IaC) \ub294 \uc218\ub3d9 \ud504\ub85c\uc138\uc2a4 \ub300\uc2e0 \ucf54\ub4dc\ub97c \ud1b5\ud574 \uc778\ud504\ub77c\ub97c \ud504\ub85c\ube44\uc800\ub2dd\ud558\uace0 \uad00\ub9ac\ud558\ub294 \ubc29\ubc95\uc785\ub2c8\ub2e4. \uae30\uc874\uc758 \uc218\ub3d9 \uc124\uc815 \ubc29\uc2dd\uc740 \ubc18\ubcf5 \uc791\uc5c5\uc774 \ub9ce\uace0 \ud734\uba3c \uc5d0\ub7ec\uac00 \ubc1c\uc0dd\ud558\uae30 \uc26c\uc6b0\uba70, \uc778\ud504\ub77c \uc124\uc815\uc744 \ubcc4\ub3c4\ub85c \ubb38\uc11c\ud654\ud574 \uad00\ub9ac\ud574\uc57c \ud558\ub294 \ubc88\uac70\ub85c\uc6c0\uc774 \uc788\uc2b5\ub2c8\ub2e4. IaC\ub294 \uc774\ub7ec\ud55c \ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574 \ub4f1\uc7a5\ud588\uc73c\uba70, \uc778\ud504\ub77c\ub97c \ucf54\ub4dc\ub85c \uad00\ub9ac\ud568\uc73c\ub85c\uc368 \uc77c\uad00\uc131\uc744 \ubcf4\uc7a5\ud558\uace0 \uc6b4\uc601 \ud6a8\uc728\uc131\uc744 \ub192\uc77c \uc218 \uc788\uc2b5\ub2c8\ub2e4.","permalink":"/docs/cs/cs-infrastructure-as-code"},{"id":"cs/cs-test-double","title":"\ud14c\uc2a4\ud2b8 \ub354\ube14 (Test Double)","description":"\ud14c\uc2a4\ud2b8 \ub354\ube14(Test Double) \uc740 \ud14c\uc2a4\ud2b8 \ucf54\ub4dc\uc5d0\uc11c \uc2e4\uc81c \uac1d\uccb4 \ub300\uc2e0 \uc0ac\uc6a9\ud558\ub294 \ub300\uccb4 \uac1d\uccb4\ub97c \uc758\ubbf8\ud569\ub2c8\ub2e4. \uc2e4\uc804\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 \uac1d\uccb4\ub97c \ud14c\uc2a4\ud2b8 \ud658\uacbd\uc5d0 \ub9de\uac8c \u201c\ub354\ube14\u201d\ub85c \ubc14\uafd4\uce58\uae30\ud574\uc11c \ud14c\uc2a4\ud2b8\ub97c \uc218\ud589\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4.","permalink":"/docs/cs/cs-test-double"},{"id":"cs/cs-trie","title":"\ud2b8\ub77c\uc774(Trie) \uc790\ub8cc \uad6c\uc870","description":"\u2714\ufe0f \ud2b8\ub77c\uc774 (Trie) \uc790\ub8cc \uad6c\uc870\ub780?","permalink":"/docs/cs/cs-trie"},{"id":"db/db-transactional-outbox-pattern","title":"\ud2b8\ub79c\uc7ad\uc154\ub110 \uc544\uc6c3\ubc15\uc2a4 \ud328\ud134 (Transactional Outbox Pattern)","description":"\ud2b8\ub79c\uc7ad\uc154\ub110 \uc544\uc6c3\ubc15\uc2a4 \ud328\ud134(Transactional Outbox Pattern) \uc740 \ubd84\uc0b0 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ub2e8\uc77c \uc791\uc5c5\uc5d0 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc4f0\uae30 \uc791\uc5c5\uacfc \uba54\uc2dc\uc9c0 \ud639\uc740 \uc774\ubca4\ud2b8 \ubc1c\ud589\uc774 \ubaa8\ub450 \ud3ec\ud568\ub41c \uacbd\uc6b0 \ubc1c\uc0dd\ud558\ub294 \uc774\uc911 \uc4f0\uae30 \ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4, \ub2e4\uc74c\uacfc \uac19\uc740 \ucf54\ub4dc\uac00 \uc874\uc7ac\ud55c\ub2e4\uace0 \uac00\uc815\ud558\uaca0\uc2b5\ub2c8\ub2e4.","permalink":"/docs/db/db-transactional-outbox-pattern"},{"id":"cs/cs-context-switch","title":"\ud504\ub85c\uc138\uc2a4\uc640 \uc2a4\ub808\ub4dc \ucee8\ud14d\uc2a4\ud2b8 \uc2a4\uc704\uce6d","description":"\u2714\ufe0f \ucee8\ud14d\uc2a4\ud2b8 \uc2a4\uc704\uce6d(Context Switching)\uc774\ub780?","permalink":"/docs/cs/cs-context-switch"},{"id":"cs/cs-functional-programming","title":"\ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d (Functional Programming)","description":"\uac1d\uccb4\uc9c0\ud5a5 \ud328\ub7ec\ub2e4\uc784\uacfc \ub9c8\ucc2c\uac00\uc9c0\ub85c \ud558\ub098\uc758 \ud504\ub85c\uadf8\ub798\ubc0d \ud328\ub7ec\ub2e4\uc784\uc73c\ub85c\uc11c,","permalink":"/docs/cs/cs-functional-programming"},{"id":"cs/cs-hash-collision","title":"\ud574\uc2dc \ucda9\ub3cc","description":"\ud574\uc2dc(Hash) \uc790\ub8cc \uad6c\uc870\ub294 \ud0a4-\uac12 \uc30d\uc73c\ub85c \uc774\ub8e8\uc5b4\uc9c4 \ub370\uc774\ud130 \uad6c\uc870\ub85c \ud0a4\ub97c \uc774\uc6a9\ud574 \uac12\uc744 O(1) \uc2dc\uac04 \ubcf5\uc7a1\ub3c4\ub85c \ucc3e\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud574\uc2dc \uc790\ub8cc \uad6c\uc870\ub294 \ud0a4\ub97c \ud574\uc2dc \ud568\uc218\uc5d0 \ub123\uc5b4\uc11c \ub098\uc624\ub294 \uacb0\uacfc\ub97c \uae30\ubc18\uc73c\ub85c \uac12\uc744 \uad00\ub9ac\ud558\ub294\ub370\uc694. \ud574\uc2dc \ud568\uc218\ub294 \ub2e4\ub978 \ud0a4\ub97c \uc0ac\uc6a9\ud574\ub3c4 \uac19\uc740 \uacb0\uacfc\uac00 \ub098\uc624\ub294 \uacbd\uc6b0\uac00 \uc874\uc7ac\ud569\ub2c8\ub2e4. \uc774\ub97c \ud574\uc2dc \ucda9\ub3cc(Hash Collision) \uc774\ub77c\uace0 \ud569\ub2c8\ub2e4.","permalink":"/docs/cs/cs-hash-collision"},{"id":"cs/cs-network-switching","title":"\ud68c\uc120 \uad50\ud658 \ubc29\uc2dd\uacfc \ud328\ud0b7 \uad50\ud658 \ubc29\uc2dd","description":"\u2714\ufe0f \ud68c\uc120 \uad50\ud658 \ubc29\uc2dd (Circuit Switching)","permalink":"/docs/cs/cs-network-switching"}],"unlisted":false}}')}}]);